<?php
if (!defined('ABSPATH')) {
    exit;
}

if (!class_exists('StbTools')) {
    class StbTools {
        private array $settings = [];
        public array $defaultSettings = [];

        public function __construct() {
            $this->defaultSettings = self::getDefaultSettings();
        }

        private function getDefaultSettings(): array {
            return [
                'themeName' => 'stb-dark',
                'roundedCorners' => true,
                'radius' => 5,
                'borderStyle' => 'solid',
                'margins' => [
                    'top' => 10,
                    'left' => 10,
                    'right' => 10,
                    'bottom' => 10,
                ],
                'bigImg' => true,
                'showImg' => true,
                'collapsing' => true,
                'collapsed' => false,
                'langDirect' => 'ltr',
                'mode' => 'css', // css | js | php
                'side' => 0,
                'imgMinus' => [
                    'image' => '',
                    'defaultImage' => 'data:image/png;base64,',
                    'enabled' => false,
                ],
                'imgPlus' => [
                    'image' => '',
                    'defaultImage' => 'data:image/png;base64,',
                    'enabled' => false,
                ],
                'duration' => 500,
                'caption' => [
                    'font' => [
                        'fontSize' => 15,
                        'fontFamily' => 'Impact, Verdana, Helvetica, Arial, sans-serif',
                    ],
                ],
                'shadow' => [
                    'enabled' => true,
                    'inset' => false,
                    'offsetX' => 0,
                    'offsetY' => 0,
                    'blur' => 15,
                    'alpha' => 0.15,
                    'color' => '#555555',
                ],
                'text' => [
                    'shadow' => [
                        'enabled' => false,
                        'offsetX' => 1,
                        'offsetY' => 1,
                        'blur' => 3,
                        'alpha' => 0.15,
                        'color' => '#000000',
                    ],
                    'font' => [
                        'fontSize' => 0,
                        'fontFamily' => '',
                    ],
                    'height' => 'inherit',
                    'customHeight' => 0,
                ],
                'deleteOptions' => 0,
                'deleteDB' => 0,
                'cssLoading' => 'dynamic' // static | styled | dynamic
            ];
        }

        /**
         * @return array
         */
        public function getSettings(): array
        {
            if (count($this->settings) === 0) {
                $this->settings = get_option('SpecialTextBoxesAdminSettings', $this->defaultSettings);
            }
            return $this->settings;
        }

        public function convertOptions($options): ?array
        {
            if (!$options) return null;
            return [
                'themeName' => 'stb-dark',
                'roundedCorners' => $options['rounded_corners'] == 'true',
                'radius' => 5,
                'borderStyle' => $options['border_style'],
                'margins' => [
                    'top' => (int)$options['top_margin'],
                    'left' => (int)$options['left_margin'],
                    'right' => (int)$options['right_margin'],
                    'bottom' => (int)$options['bottom_margin'],
                ],
                'bigImg' => $options['bigImg'] == 'true',
                'showImg' => $options['showImg'] == 'true',
                'collapsing' => $options['collapsing'] == 'true',
                'collapsed' => $options['collapsed'] == 'true',
                'langDirect' => $options['langDirect'] ?? 'ltr',
                'mode' => $options['mode'] ?? 'css',
                'side' => (int)$options['side'],
                'imgMinus' => [
                    'image' => $options['imgMinus'] ?? '',
                    'defaultImage' => 'data:image/png;base64,',
                    'enabled' => 'false',
                ],
                'imgPlus' => [
                    'image' => $options['imgPlus'] ?? '',
                    'defaultImage' => 'data:image/png;base64,',
                    'enabled' => 'false',
                ],
                'duration' => (int)$options['js_duration'] ?? 500,
                'caption' => [
                    'font' => [
                        'fontSize' => (int)$options['captionFontSize'] ?? 12,
                        'fontFamily' => $options['fontFamily'] ?? 'Impact, Verdana, Helvetica, Arial, sans-serif',
                    ],
                ],
                'shadow' => [
                    'enabled' => $options['js_shadow_enabled'] == 'true',
                    'inset' => false,
                    'offsetX' => (int)$options['js_shadow_offsetX'],
                    'offsetY' => (int)$options['js_shadow_offsetY'],
                    'blur' => (int)$options['js_shadow_blur'] ?? 15,
                    'alpha' => (float)$options['js_shadow_alpha'] ?? 0.15,
                    'color' => '#' . $options['js_shadow_color'] ?? '555555',
                ],
                'text' => [
                    'shadow' => [
                        'enabled' => $options['js_textShadow_enabled'] == 'true',
                        'offsetX' => (int)$options['js_textShadow_offsetX'] ?? 1,
                        'offsetY' => (int)$options['js_textShadow_offsetY'] ?? 1,
                        'blur' => (int)$options['js_textShadow_blur'] ?? 3,
                        'alpha' => (float)$options['js_textShadow_alpha'] ?? 0.15,
                        'color' => '#' . $options['js_textShadow_color'] ?? '000000',
                    ],
                    'font' => [
                        'fontSize' => (int)$options['fontSize'],
                        'fontFamily' => $options['fontFamily'] ?? 'Impact, Verdana, Helvetica, Arial, sans-serif',
                    ],
                    'height' => $options['js_text_height'] ?? 'inherit',
                    'customHeight' => (int)$options['js_custom_text_height'],
                ],
                'deleteOptions' => (int)$options['deleteOptions'],
                'deleteDB' => (int)$options['deleteDB'],
                'cssLoading' => $options['css_loading'] ?? 'dynamic'
            ];
        }
    }
}